package cs101;

import java.io.BufferedReader; import java.io.FileReader; import java.util.Scanner; /** * Luis Paolo Say * 3csd-39 * 3-CSD * Lab Exercise 4 */ public class NewClass { public static void quickSort(String[] a, int i, int j, String choice){ int q=0; if(i<j){ if(choice.equalsIgnoreCase("a")){ q = partitionA(a, i, j); } if(choice.equalsIgnoreCase("d")){ q = partitionD(a, i, j); } quickSort(a, i, q, choice); quickSort(a, q+1, j, choice); } } public static int partitionA(String[] a, int i, int j){ String p=a[i]; while(i<j){ while(a[i].compareTo(p) < 0){ i++; } while(a[j].compareTo(p) > 0){ j--; } String temp=a[i]; a[i]=a[j]; a[j]=temp; } return i; } public static int partitionD(String[] a, int i, int j){ String p=a[i]; while(i<j){ while(a[i].compareTo(p) > 0){ i++; } while(a[j].compareTo(p) < 0){ j--; } String temp=a[i]; a[i]=a[j]; a[j]=temp; } return i; } static Scanner s = new Scanner(System.in); public static void main(String[] args) throws Exception { try{ System.out.print("Enter max number of items: "); int max = s.nextInt(); String[] word = new String[max]; int i=0; String inputFile = "src\\ioc.txt"; FileReader rFile = new FileReader(inputFile); BufferedReader sFile = new BufferedReader(rFile); System.out.println("Unsorted List: "); do{ word[i]=sFile.readLine(); if(word[i]==null){ break; } i++; }while(i<max); max=i; for(i=0; i<max; i++){ System.out.format("%2d. " + word[i] + "\n", i+1); } Scanner forString = new Scanner(System.in); System.out.print("Choose sorting order: (a) Ascending (d) Descending - "); String choice = forString.next(); System.out.print("Choose sorting method: (1) Quick Sort - "); int t = s.nextInt(); if(t==1){ quickSort(word, 0, max-1, choice); } System.out.println("Sorted List: "); for(i=0; i<max; i++){ System.out.format("%2d. " + word[i] + "\n", i+1); } rFile.close(); } catch(Exception e){ e.printStackTrace(System.out); } } }